<?php

namespace PatientBundle\Entity\Repository;

/**
 * PatientRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class PatientRepository extends \Doctrine\ORM\EntityRepository
{
    public function countObjectsPerDay()
    {
        $today = (new \DateTime)->setTime(0,0,0);
        $cloneDate = clone $today;
        $nextday = $cloneDate->modify('+1 day');

        return $this->getEntityManager()
            ->createQueryBuilder()
            ->select('COUNT(p)')
            ->from('PatientBundle:Patient', 'p')
            ->andWhere('p.createdAt >= :today')
            ->andWhere('p.createdAt < :nextday')
            ->setParameters(['today' => $today, 'nextday' => $nextday])
            ->getQuery()
            ->getSingleScalarResult();
    }
}
